SELECT     COB_DATE,     CURRENCY_OF_MEASURE,     CURVE_NAME,     CURVE_TYPE,     CASE          WHEN a.TERM_OF_MEASURE <= 365 THEN '0-1y'         WHEN a.TERM_OF_MEASURE > 365 AND a.TERM_OF_MEASURE <= 1825 THEN '1-5y'         WHEN a.TERM_OF_MEASURE > 1825 AND a.TERM_OF_MEASURE <= 3650 THEN '5-10y'         WHEN a.TERM_OF_MEASURE > 3650 AND a.TERM_OF_MEASURE <= 5475 THEN   '10-15y'     ELSE '15+y' END AS TERM_NEW_GROUP,     SUM (CAST(USD_IRPV01SPRD AS DOUBLE PRECISION)) AS USD_IR_UNIFIED_PV01 FROM     cdwuser.U_IR_MSR_INTRPLT a WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and A.CCC_PL_REPORTING_REGION in ('ASIA PACIFIC') AND        CCC_BANKING_TRADING='TRADING' AND     CCC_DIVISION IN ('FIXED INCOME DIVISION') AND     /*a.CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND*/     /*SECTYPE2  NOT IN ('GOVTS', 'CORPBONDS','CAP', 'FLOOR', 'SWAP', 'ZCS', 'CAPS', 'FLOORS', 'SWAPS', 'ZCS') AND */     CURVE_TYPE<>'FXBASISYIELDCURVE' AND     /*CURRENCY_OF_MEASURE='USD' AND*/     USD_IRPV01SPRD IS NOT NULL GROUP BY     CURRENCY_OF_MEASURE,     COB_DATE,     CURVE_NAME,     CURVE_TYPE,     CASE          WHEN a.TERM_OF_MEASURE <= 365 THEN '0-1y'         WHEN a.TERM_OF_MEASURE > 365 AND a.TERM_OF_MEASURE <= 1825 THEN '1-5y'         WHEN a.TERM_OF_MEASURE > 1825 AND a.TERM_OF_MEASURE <= 3650 THEN '5-10y'         WHEN a.TERM_OF_MEASURE > 3650 AND a.TERM_OF_MEASURE <= 5475 THEN   '10-15y'     ELSE '15+y' END