select      a.COB_DATE,      a.VAL_IN,      a.VAL_FOR,      a.CCC_PRODUCT_LINE,      a.CURRENCY_OF_POSITION,      a.CURRENCY_CODE,   case      when a.val_in <=  1 then 1      when a.val_in <= 2 then 2      when a.val_in <= 5 then 5       when a.val_in <= 10 then 10  else 20 end as val_in_adj,  case      when a.val_for <=  1 then  1      when a.val_for <=  2 then  2      when a.val_for <= 5 then 5       when a.val_for <= 10 then 10      when a.val_for <= 20 then 20  else 30 end as val_for_adj,   a.GRID_MEASURE_NAME,  sum(a.GRID_MEASURE_VALUE) as USD_KAPPA   from cdwuser.u_grid_msr a   where (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and      a.CCC_DIVISION = 'FIXED INCOME DIVISION' and     /*a.CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND*/ A.CCC_PL_REPORTING_REGION in ('ASIA PACIFIC') AND       (a.VAR_EXCL_FL <> 'Y' AND a.MEASURE_VAR_EXCL_FL <> 'Y' ) and       CCC_BANKING_TRADING='TRADING' AND      a.GRID_MEASURE_NAME = 'USD_KAPPA'   group by      a.COB_DATE,      a.VAL_IN,      a.VAL_FOR,      a.CCC_PRODUCT_LINE,      a.CURRENCY_OF_POSITION ,      a.CURRENCY_CODE,      a.GRID_MEASURE_NAME