'NYM HO', 'NYM RBOB',                                                          'SING 180FO', 'SING 380FO', 'US C2', 'WTI', 'NYM WTI') THEN 'OIL' WHEN          prod_pos_name_description IN ('AH', 'CA', 'NI', 'PB', 'SN', 'ZS', 'IRON ORE') THEN 'BASE METAL' WHEN prod_pos_name_description          IN ('GOLD FORWARD', 'GOLD FUTURE', 'PALLADIUM FOR', 'PALLADIUM FORWARD', 'PALLADIUM FUTURE', 'PLATINUM FORW', 'PLATINUM FORWARD'         , 'PLATINUM FUTURE', 'SILVER FORWAR', 'SILVER FORWARD', 'XAU', 'XPD') THEN 'PRECIOUS METAL' WHEN prod_pos_name_description IN (                                                                                                                                            'COCOA'         , 'COFFEE', 'CORN', 'COTTON', 'KANSASWHEAT', 'LIFFE SUGAR', 'RAST-CHIBA VL', 'RAST-CHIBA VLCC', 'SOYBEANS', 'SUGAR', 'WHEAT',                                                                                                                                             'AMST-NY 37MM'         , 'BDI', 'SOYBEAN MEAL', 'SOYBEAN OIL') THEN 'OTHER'     ELSE 'NOT IN' END AS CM_Category,     CASE WHEN PRODUCT_SUB_TYPE_CODE LIKE ('%FLRVA%') THEN 'FLRVA' WHEN PRODUCT_SUB_TYPE_CODE = 'CTDVA' THEN 'CTDVA' WHEN          PRODUCT_SUB_TYPE_CODE = 'LVA' THEN 'LVA' WHEN PRODUCT_SUB_TYPE_CODE = 'OISVA' THEN 'OISVA' WHEN     CCC_STRATEGY_DETAILS IN ('INSURANCE PRODUCTS') AND     BOOK IN ('NEWLD', 'NEWLC')     THEN 'NEW LOC' WHEN     CCC_STRATEGY_DETAILS IN ('INSURANCE PRODUCTS') AND     BOOK IN ('CHM2M', 'CHINS')     THEN 'NEW LOC HEDGES' WHEN (PRODUCT_SUB_TYPE_CODE IN ('MNE_CVA', 'MNE', 'MNE_PROXY') OR                                 (BOOK LIKE 'CVN%' AND                                  BU_RISK_SYSTEM LIKE 'STS%')) THEN 'MNE CVA' WHEN (CCC_STRATEGY_DETAILS LIKE '%MNE CVA RISK MGMT%' AND                                                                                    BOOK NOT IN ('CVAEM', 'CV2NB')) THEN 'MNE CVA' WHEN (                                                                                                                                             PRODUCT_SUB_TYPE_CODE                                                                                                                                              IN                                                                                                                                              (                                                                                                                                                  'MPE_FVA_RAW'                                                                                                                                             ,                                                                                                                                                   'MPE_FVA'                                                                                                                                             ,                                                                                                                                                   'MPE_FVA_PROXY'                                                                                                                                             )                                                                                                                                              OR                                                                                                                                         (                                                                                                                                              BOOK                                                                                                                                               LIKE                                                                                                                                               'FVP%'                                                                                                                                               AND                                                                                                                                          BU_RISK_SYSTEM                                                                                                                                               LIKE                                                                                                                                               'STS%'                                                                                                                                             )         ) THEN 'MPE FVA' WHEN (CCC_STRATEGY_DETAILS LIKE '%MPE FVA RISK MGMT%' AND                                BOOK NOT IN ('CVAEM', 'CV2NB')) THEN 'MPE FVA' WHEN (PRODUCT_SUB_TYPE_CODE IN ('MNE_FVA', 'MNE_FVA_NET')                                                                                          OR                                                                                     (BOOK LIKE 'FVN%' AND                                                                                      BU_RISK_SYSTEM LIKE 'STS%')) THEN 'MNE FVA' WHEN (                                                                                                                                            CCC_STRATEGY_DETAILS                                                                                                                                             LIKE                                                                                                                                             '%MNE FVA RISK MGMT%'                                                                                                                                             AND                                                                                                                                        BOOK                                                                                                                                             NOT                                                                                                                                             IN                                                                                                                                             (                                                                                                                                                 'CVAEM'                                                                                                                                            ,                                                                                                                                                  'CV2NB'                                                                                                                                            )         ) THEN 'MNE FVA' WHEN (PRODUCT_SUB_TYPE_CODE IN ('MPE_CVA', 'MPE', 'MPE_PROXY', 'MNE_CP') OR                                (BOOK LIKE 'CVP%' AND                                 BU_RISK_SYSTEM LIKE 'STS%')) THEN 'MPE CVA' WHEN (CCC_STRATEGY_DETAILS LIKE '%MPE CVA RISK MGMT%' AND                                                                                   BOOK NOT IN ('CVAEM', 'CV2NB')) THEN 'MPE CVA' WHEN (                                                                                                                                            CCC_STRATEGY_DETAILS                                                                                                                                             LIKE                                                                                                                                             (                                                                                                                                                 '%HEDGES%'                                                                                                                                            )                                                                                                                                             OR                                                                                                                                        BOOK =                                                                                                                                             'BHCDS'         ) THEN 'HEDGES'     ELSE 'OTHER' END AS Type_Flag,     SUM (a.USD_CM_KAPPA) AS CM_KAPPA FROM cdwuser.U_CM_MSR a WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-01-31') and CCC_PL_REPORTING_REGION in ('EMEA') AND      a.USD_CM_KAPPA IS NOT NULL AND     (a.CCC_BUSINESS_AREA IN ('CPM', 'CPM TRADING (MPE)', 'CREDIT', 'MS CVA MNE - FID', 'MS CVA MNE - COMMOD') OR      a.CCC_STRATEGY IN ('MS CVA MPE - DERIVATIVES', 'MS CVA MNE - DERIVATIVES','EQ XVA HEDGING')) AND     a.ccc_product_line NOT IN ('CREDIT LOAN PORTFOLIO', 'CMD STRUCTURED FINANCE') GROUP BY     a.COB_date,CM_Category,Type_Flag