SELECT COB_DATE, CCC_DIVISION, CCC_PRODUCT_LINE, SUM(a.USD_PV01SPRD) as pv01sprd, CASE WHEN a.CCC_DIVISION IN ('FID DVA','FIC DVA') THEN sum(a.USD_PV10_BENCH) ELSE sum(a.USD_CREDIT_PV10PCT) END AS USD_PV10Bench FROM CDWUSER.U_EXP_MSR a WHERE a.COB_DATE IN ('2018-02-28','2018-01-31') AND (a.CCC_DIVISION = 'FID DVA' OR a.CCC_DIVISION='FIC DVA' OR CCC_STRATEGY = 'MS DVA STR NOTES IED') GROUP BY COB_DATE, CCC_DIVISION, CCC_PRODUCT_LINE