SELECT A.COB_DATE, CASE WHEN A.CCC_BANKING_TRADING <> 'TRADING' THEN 'BANKING' ELSE 'TRADING' END AS BANKING_TRADING_FLAG, SUM (A.USD_DELTA) AS DELTA FROM CDWUSER.U_DM_EQ A WHERE a.cob_date IN ('02/28/2018', '02/27/2018') AND A.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND A.CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') GROUP BY A.COB_DATE, CASE WHEN A.CCC_BANKING_TRADING <> 'TRADING' THEN 'BANKING' ELSE 'TRADING' END