SELECT A.COB_DATE, CASE WHEN CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') THEN 'MSCO' WHEN CCC_TAPS_COMPANY in ('0111') THEN 'MSCS' ELSE CCC_TAPS_COMPANY END AS LE, SUM (A.USD_PV10_BENCH) AS USD_PV10_BENCH, 0 AS USD_PV10_BENCH_GCT_NIG FROM CDWUSER.U_EXP_MSR A WHERE a.CCC_TAPS_COMPANY in ( '0201','0103','0205','0206','0530','5924','0111') AND A.cob_date in ( '02/28/2018', '02/27/2018') AND A.CCC_DIVISION = 'FIXED INCOME DIVISION' AND ((A.PRODUCT_TYPE_CODE IN ('CDSOPTIDX', 'CRDINDEX', 'LOANINDEX')) OR (A.CCC_PRODUCT_LINE NOT IN ('DISTRESSED TRADING'))) AND ((A.FID1_SENIORITY NOT IN ('SUBT1', 'SUBUT2', 'AT1')) OR (A.PRODUCT_TYPE_CODE NOT IN ('BOND', 'BONDFUT', 'BONDFUTOPT', 'BONDIL', 'BONDOPT', 'PREF'))) AND ((a.COUNTRY_CD_OF_RISK NOT IN ('USA')) OR (a.FID1_INDUSTRY_NAME_LEVEL2 NOT IN ('AGENCIES'))) AND a.FID1_INDUSTRY_NAME_LEVEL1 NOT IN ('SOVEREIGN') AND A.PRODUCT_TYPE_CODE NOT IN ('ARSMUNI', 'ARSPREF', 'ARSSL', 'MUNI', 'TOB') AND a.COUNTRY_OF_RISK_TIER NOT IN ('2') AND a.BOOK NOT IN ('CPYRM', 'SCCDS', 'TOTMT', 'SCALL') AND A.CCC_STRATEGY NOT LIKE '%EVENT' AND A.CCC_STRATEGY NOT LIKE 'EVENT%' AND A.CCC_STRATEGY NOT LIKE '%CVA' AND A.CCC_STRATEGY NOT LIKE 'CVA%' AND A.CCC_STRATEGY NOT IN ('CPM OTHER', 'TRADE CLAIMS', 'DERIVATIVES FUNDING') AND A.CCC_BUSINESS_AREA NOT IN ('LENDING', 'MS CVA MNE - FID','COMMODITIES') GROUP BY A.COB_DATE, CASE WHEN CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') THEN 'MSCO' WHEN CCC_TAPS_COMPANY in ('0111') THEN 'MSCS' ELSE CCC_TAPS_COMPANY END union all SELECT B.COB_DATE, CASE WHEN CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') and book not in ('DCMSA') THEN 'MSCO' WHEN CCC_TAPS_COMPANY in ('0111') THEN 'MSCS' ELSE CCC_TAPS_COMPANY END AS LE, 0 as USD_PV10_BENCH, SUM (B.USD_PV10_BENCH) AS USD_PV10_BENCH_GCT_NIG FROM CDWUSER.U_EXP_MSR B WHERE CCC_TAPS_COMPANY in ( '0201','0103','0205','0206','0530','5924','0111') AND cob_date in ( '02/28/2018', '02/27/2018') AND B.CCC_DIVISION = 'FIXED INCOME DIVISION' AND ((B.PRODUCT_TYPE_CODE IN ('CDSOPTIDX', 'CRDINDEX', 'LOANINDEX')) OR (B.CCC_PRODUCT_LINE NOT IN ('DISTRESSED TRADING'))) AND ((B.FID1_SENIORITY NOT IN ('SUBT1', 'SUBUT2', 'AT1')) OR (B.PRODUCT_TYPE_CODE NOT IN ('BOND', 'BONDFUT', 'BONDFUTOPT', 'BONDIL', 'BONDOPT', 'PREF'))) AND B.PRODUCT_TYPE_CODE NOT IN ('ARSMUNI', 'ARSPREF', 'MUNI', 'TOB') AND B.COUNTRY_OF_RISK_TIER NOT IN ('2') AND B.CCC_BUSINESS_AREA IN ('CREDIT-CORPORATES','CREDIT CORPORATES PRIMARY', 'DSP - CREDIT' /*, 'FXEM MACRO TRADING', 'EM CREDIT TRADING', 'MUNICIPAL SECURITIES'*/) AND B.CCC_STRATEGY NOT LIKE 'EVENT%' AND B.CCC_STRATEGY NOT LIKE '%EVENT' AND B.MRD_RATING IN ('B', 'BB', 'C', 'CC', 'CCC', 'D', 'NR') AND B.book not in ('TOTMT') GROUP BY B.COB_DATE, CASE WHEN CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') and book not in ('DCMSA') THEN 'MSCO' WHEN CCC_TAPS_COMPANY in ('0111') THEN 'MSCS' ELSE CCC_TAPS_COMPANY END