Select COB_DATE, Case when CURRENCY_OF_MEASURE in ('EUR','GBP','USD','JPY','CHF') then CURRENCY_OF_MEASURE else 'Others' end as CURRENCY_OF_MEASURE, sum(USD_IR_KAPPA/10) as usd_kappa, sum(USD_IR_THETA) as usd_theta FROM cdwuser.U_IR_MSR a WHERE cob_date in ('2018-02-28','2018-02-27') AND ccc_product_line IN ('STRUCTURED RATES - EU') group by COB_DATE, Case when CURRENCY_OF_MEASURE in ('EUR','GBP','USD','JPY','CHF') then CURRENCY_OF_MEASURE else 'Others' end