R' THEN 'UNITED KINGDOM' ELSE 'OTHER' END AS COUNTRY_CD_OF_RISK ,CASE WHEN CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' THEN 'SPG' ELSE CCC_BUSINESS_AREA END AS CCC_BUSINESS_AREA ,TERM_NEW ,SUM(a.USD_PV01SPRD) AS COUNTRY_PV01 FROM cdwuser.U_DM_IR a WHERE cob_date in ('2018-02-28','2018-02-27') AND CCC_BUSINESS_AREA in ('STRUCTURED RATES') AND CCC_PL_REPORTING_REGION = 'EMEA' AND CCC_division IN ('FIXED INCOME DIVISION') AND USD_PV01SPRD IS NOT NULL AND USD_PV01SPRD <> 0 AND CCC_BUSINESS_AREA NOT IN ('LENDING') AND CCC_BUSINESS_AREA NOT LIKE '%CVA%' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%' AND A.VERTICAL_SYSTEM IN ('C1_LN', 'C1_NY', 'C1_TK') AND COUNTRY_CD_OF_RISK IN ('IRL','NLD', 'CHE', 'SWE', 'NOR', 'AUT', 'GBR', 'FIN', 'DNK', 'BEL', 'DEU', 'FRA') AND A.FID1_INDUSTRY_NAME_LEVEL1 = 'SOVEREIGN' GROUP BY cob_date ,CASE WHEN COUNTRY_CD_OF_RISK = 'IRL' THEN 'IRELAND' WHEN COUNTRY_CD_OF_RISK = 'DEU' THEN 'GERMANY' WHEN COUNTRY_CD_OF_RISK = 'FRA' THEN 'FRANCE' WHEN COUNTRY_CD_OF_RISK = 'BEL' THEN 'BELGIUM' WHEN COUNTRY_CD_OF_RISK = 'NLD' THEN 'NETHERLANDS' WHEN COUNTRY_CD_OF_RISK = 'AUT' THEN 'AUSTRIA' WHEN COUNTRY_CD_OF_RISK = 'GBR' THEN 'UNITED KINGDOM' ELSE 'OTHER' END ,CASE WHEN CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' THEN 'SPG' ELSE CCC_BUSINESS_AREA END ,TERM_NEW