SELECT cob_date, PRODUCT_TYPE_CODE,ccc_product_line,ccc_strategy, FID1_IDENTIFIER, issuer_country_code, TAPSCUSIP, PRODUCT_DESCRIPTION, SUM (LCY_NOTIONAL) AS Lcy_Notional, SUM (USD_NOTIONAL) AS Notional, ABS (SUM (USD_NOTIONAL)) AS Abs_Notional FROM cdwuser.U_EXP_MSR f WHERE f.cob_date in ('2018-02-28') AND f.VAR_EXCL_FL = 'N' AND CCC_BUSINESS_AREA IN ('STRUCTURED RATES') AND product_type_code IN ('AGN', 'BOND', 'GVTBOND', 'GVTBONDIL', 'GVTFRN') AND PRODUCT_DESCRIPTION NOT LIKE '%MS%' AND PRODUCT_DESCRIPTION NOT LIKE '%Strip%' AND POSITION_ULT_ISSUER_PARTY_DARWIN_NAME NOT IN ('MORGAN STANLEY') GROUP BY cob_date, TAPSCUSIP, PRODUCT_DESCRIPTION, FID1_IDENTIFIER, PRODUCT_TYPE_CODE,ccc_product_line,ccc_strategy, issuer_country_code