SELECT a.cob_date ,SUM(a.USD_EXPOSURE) AS NET_EXP ,ABS(SUM(a.USD_EXPOSURE)) AS ABS_NET_EXP ,a.TAPSCUSIP FROM cdwuser.U_CR_MSR a WHERE cob_date in ('2018-02-28','2018-02-27') AND CCC_BUSINESS_AREA in ('STRUCTURED RATES') AND CCC_PL_REPORTING_REGION = 'EMEA' AND CCC_division IN ('FIXED INCOME DIVISION') AND USD_EXPOSURE IS NOT NULL AND USD_EXPOSURE <> 0 AND CCC_BUSINESS_AREA NOT IN ('LENDING') AND CCC_BUSINESS_AREA NOT LIKE '%CVA%' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%' AND VERTICAL_SYSTEM NOT IN ('C1_LN', 'C1_NY', 'C1_TK') AND COUNTRY_CD_OF_RISK IN ('IRL','NLD', 'CHE', 'SWE', 'NOR', 'AUT', 'GBR', 'FIN', 'DNK', 'BEL', 'DEU', 'FRA') AND TAPSCUSIP IS NOT NULL GROUP BY a.cob_date ,a.TAPSCUSIP