SELECT a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.CCC_STRATEGY, a.CCC_TAPS_COMPANY, sum(a.USD_EQ_DELTA_DECOMP) as USD_EQ_DELTA_DECOMP, sum(a.USD_CM_DELTA_DECOMP) as USD_CM_DELTA_DECOMP FROM CDWUSER.U_DM_EQ a WHERE a.COB_DATE in ('2018-02-28','2018-02-27') AND a.CCC_BANKING_TRADING = 'TRADING' AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.CCC_PL_REPORTING_REGION = 'EMEA' GROUP BY a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.CCC_STRATEGY, a.CCC_TAPS_COMPANY