SELECT a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.CCC_STRATEGY, a.CCC_TAPS_COMPANY, sum(a.USD_DELTA) as USD_DELTA, sum(a.USD_DIV_RHO) as USD_DIV_RHO, sum(a.USD_EQ_KAPPA) as USD_EQ_KAPPA, sum(a.USD_EQ_KAPPA_PLS100BP_TIMEADJ) as USD_EQ_TA_KAPPA, sum(a.SLIDE_KAPPA_MIN_20_USD) as SLIDE_KAPPA_MIN_20_USD, sum(a.SLIDE_KAPPA_PLS_20_USD) as SLIDE_KAPPA_PLS_20_USD, sum(a.USD_THETA_GAMMA) as USD_THETA_GAMMA, sum(a.USD_EQ_GAMMA) as USD_EQ_GAMMA, sum(a.CORR_EQEQ)/1000000 as CORR_EQEQ, sum(a.SLIDE_EQ_MIN_20_USD) + 0.2* sum(a.USD_DELTA) as D20, sum(a.SLIDE_EQ_MIN_05_USD) + 0.05* sum(a.USD_DELTA) as D05 FROM CDWUSER.U_EXP_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-02-27') AND a.LE_GROUP = 'UK' AND a.CCC_BANKING_TRADING = 'TRADING' AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' GROUP BY a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.CCC_STRATEGY, a.CCC_TAPS_COMPANY