SELECT a.COB_DATE, sum(a.USD_EQ_KAPPA) as USD_EQ_KAPPA FROM CDWUSER.U_EQ_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-02-27') AND a.CCC_PL_REPORTING_REGION = 'EMEA' AND a.CCC_BANKING_TRADING = 'TRADING' AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.EXECUTIVE_MODEL LIKE '%VARIANCE%' GROUP BY a.COB_DATE