SELECT a.COB_DATE, a.CCC_PL_REPORTING_REGION, sum(coalesce(a.SLIDE_EQ_MIN_10_USD,0)) + sum(coalesce(a.SLIDE_CM_MIN_10_USD,0)) as SLIDE_EQ_MIN_10_USD, sum(coalesce(a.SLIDE_EQ_MIN_20_USD,0)) + sum(coalesce(a.SLIDE_CM_MIN_20_USD,0)) as SLIDE_EQ_MIN_20_USD, sum(coalesce(a.SLIDE_EQ_MIN_30_USD,0)) + sum(coalesce(a.SLIDE_CM_MIN_30_USD,0)) as SLIDE_EQ_MIN_30_USD FROM CDWUSER.U_DM_EQ a WHERE a.COB_DATE in ('2018-02-28','2018-01-31') AND a.CCC_BANKING_TRADING = 'TRADING' AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' GROUP BY a.COB_DATE, a.CCC_PL_REPORTING_REGION