SELECT e.COB_DATE, e.DIVISION||' - '||e.ccc_business_area as DIVISION, SUM(COALESCE(e.SLIDE_EQ_MIN_30_USD,0))/1000 AS RAW_SPOT_D_30, SUM(COALESCE(e.SLIDE_EQ_MIN_20_USD,0))/1000 AS RAW_SPOT_D_20, SUM(COALESCE(e.SLIDE_EQ_MIN_10_USD,0))/1000 AS RAW_SPOT_D_10, SUM(COALESCE(e.SLIDE_EQ_MIN_05_USD,0))/1000 AS RAW_SPOT_D_5, 0 as Base, SUM(COALESCE(e.SLIDE_EQ_PLS_05_USD,0))/1000 AS RAW_SPOT_P_5, SUM(COALESCE(e.SLIDE_EQ_PLS_10_USD,0))/1000 AS RAW_SPOT_P_10, SUM(COALESCE(e.SLIDE_EQ_PLS_20_USD,0))/1000 AS RAW_SPOT_P_20 FROM CDWUSER.U_EQ_MSR e WHERE e.COB_DATE IN ('2018-02-28') AND e.DIVISION<>'IED' AND e.LE_GROUP='UK' GROUP BY e.COB_DATE, e.DIVISION||' - '||e.ccc_business_area ORDER BY ABS(SUM(COALESCE(e.SLIDE_EQ_MIN_30_USD,0))) DESC FETCH FIRST 5 ROWS ONLY