SELECT a.COB_DATE, a.ISSUER_COUNTRY_CODE_DECOMP, CASE WHEN ISSUER_COUNTRY_CODE_DECOMP IN ( 'ALB','AND','AUT','BLR' ,'BEL','BIH','BGR','CYP' ,'HRV','CZE','DNK','EST' ,'FRO','FIN','FRA','GEO' ,'DEU','GRC','GGY','VAT' ,'HUN','ISL','IRL','IMN' ,'ITA','JEY','LVA','LIE' ,'LTU','LUX','MKD','MLT' ,'MDA','MCO','MNE','NLD' ,'NOR','POL','PRT','ROU' ,'RUS','SMR','SRB','SVK' ,'SVN','ESP','SJM','SWE' ,'CHE','UKR') THEN 'Continental Europe' WHEN ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK' ELSE 'Rest of the World' END AS REGIONAL_CLASSIFICATION, sum(coalesce(a.USD_EQ_DELTA_DECOMP,0)) as USD_EQ_DELTA_DECOMP FROM CDWUSER.U_DECOMP_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-01-31') AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.CCC_PL_REPORTING_REGION = 'EMEA' AND a.CCC_BANKING_TRADING = 'TRADING' GROUP BY a.COB_DATE, a.ISSUER_COUNTRY_CODE_DECOMP, CASE WHEN ISSUER_COUNTRY_CODE_DECOMP IN ( 'ALB','AND','AUT','BLR' ,'BEL','BIH','BGR','CYP' ,'HRV','CZE','DNK','EST' ,'FRO','FIN','FRA','GEO' ,'DEU','GRC','GGY','VAT' ,'HUN','ISL','IRL','IMN' ,'ITA','JEY','LVA','LIE' ,'LTU','LUX','MKD','MLT' ,'MDA','MCO','MNE','NLD' ,'NOR','POL','PRT','ROU' ,'RUS','SMR','SRB','SVK' ,'SVN','ESP','SJM','SWE' ,'CHE','UKR') THEN 'Continental Europe' WHEN ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK' ELSE 'Rest of the World' END