Select RANK() OVER(ORDER BY abs(sum(a.USD_EQ_KAPPA)) DESC) as RANK, a.UNDERLIER_TICK||'.'||a.UNDERLIER_EXCH as MdSymbol, sum(a.USD_EQ_KAPPA)/1000 as Vega From CDWUSER.U_EQ_MSR a Where a.COB_DATE ='2018-02-28' And a.CCC_RISK_MANAGER_LOGIN = 'freemric' And a.ACCOUNT like '077%' GRoup by a.UNDERLIER_TICK||'.'||a.UNDERLIER_EXCH HAVING abs(sum(a.USD_EQ_KAPPA))>0