SELECT DISTINCT p.ccc_pl_reporting_region as pl_reporting_region, p.ccc_business_area as business_area, p.ccc_product_line as product_line, p.ccc_strategy as strategy, p.risk_manager_id as trader FROM cdwuser.u_pct_pnl_current p WHERE p.ccc_division = 'INSTITUTIONAL EQUITY DIVISION' AND p.cob_date >= '2017-03-01' AND p.cob_date <= '2018-02-28' AND NOT p.cob_date='2017-01-03' AND (p.account_purpose_code <> 'J' OR p.account_purpose_code IS NULL) AND daily_pl <> 0 AND p.risk_manager_id IS NOT NULL ORDER BY p.ccc_pl_reporting_region, p.ccc_business_area, p.ccc_product_line, p.ccc_strategy, p.risk_manager_id