SELECT q.cob_date, q.ccc_pl_reporting_region, q.ccc_division, q.ccc_business_area, q.ccc_strategy, q.risk_manager_id, q.book, q.account, q.financial_element, sum(q.daily_pl)/1000000 as daily_pl, sum(q.ytd_pl)/1000000 as ytd_pl FROM cdwuser.u_pct_pnl_current q WHERE q.ccc_division = 'INSTITUTIONAL EQUITY DIVISION' AND q.cob_date >= '2018-01-01' AND NOT q.COB_DATE = '2017-01-03' AND (q.account_purpose_code <> 'J' OR q.account_purpose_code IS NULL) AND q.risk_manager_id is null GROUP bY q.cob_date, q.ccc_pl_reporting_region, q.ccc_division, q.ccc_business_area, q.ccc_strategy, q.risk_manager_id, q.book, q.account, q.financial_element