With x as (SELECT DISTINCT DENSE_RANK() OVER(ORDER BY p.COB_DATE) as RANK, COB_DATE FROM cdwuser.U_PCT_PNL_CURRENT p WHERE (p.COB_DATE > '2018-01-01' AND p.COB_DATE <= '2018-02-28' AND NOT p.COB_DATE = '2017-01-03') AND p.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND p.FINANCIAL_ELEMENT not in ('AGENCY', 'MSCAP FEES', 'MSCI FEES', 'RISK') ), y as ( SELECT RANK, PL_REPORTING_REGION, BUSINESS_AREA, PRODUCT_LINE, STRATEGY, VAR05, DAILY_PNL, CASE WHEN DAILY_PNL < 0 AND -DAILY_PNL > VAR05 THEN 1 ELSE 0 END AS VaRBREACH FROM ( SELECT RANK, PL_REPORTING_REGION, BUSINESS_AREA, PRODUCT_LINE, STRATEGY, sum(VAR05) as VAR05, sum(DAILY_PNL) as DAILY_PNL FROM ( SELECT DENSE_RANK() OVER(ORDER BY v.COB_DATE) as RANK, COB_DATE, LEVEL1 AS PL_REPORTING_REGION, CASE WHEN LEVEL2 = 'DELTA ONE' THEN 'CASH EQUITIES' ELSE LEVEL2 END as BUSINESS_AREA, LEVEL3 AS PRODUCT_LINE, LEVEL4 AS STRATEGY, -round(VAR05/1000,2) as VAR05, 0 as DAILY_PNL FROM cdwuser.U_VAR_ID_FLAT_VIEW v WHERE (hierarchy_group_name='EQUITY_CV') AND (hierarchy_name='region') AND (level4 IS NOT NULL) AND (level5 is NULL) AND (COB_DATE > '2018-01-01' AND COB_DATE <= '2018-02-28') UNION ALL SELECT DENSE_RANK() OVER(ORDER BY p.COB_DATE) AS RANK, p.COB_DATE AS COB_DATE, p.CCC_PL_REPORTING_REGION, p.CCC_BUSINESS_AREA, p.CCC_PRODUCT_LINE, p.CCC_STRATEGY, 0 AS VAR05, sum(round(DAILY_PL/1000000,2)) AS DAILY_PNL FROM cdwuser.U_PCT_PNL_CURRENT p WHERE (COB_DATE > '2018-01-01' AND COB_DATE <= '2018-02-28' AND NOT p.COB_DATE = '2017-01-03') AND p.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND (p.ACCOUNT_PURPOSE_CODE <> 'J' or p.ACCOUNT_PURPOSE_CODE IS NULL) AND p.FINANCIAL_ELEMENT not in ('AGENCY', 'MSCAP FEES', 'MSCI FEES', 'RISK') GROUP BY p.COB_DATE, p.CCC_PL_REPORTING_REGION, p.CCC_BUSINESS_AREA, p.CCC_PRODUCT_LINE, p.CCC_STRATEGY ) z GROUP BY RANK, PL_REPORTING_REGION, BUSINESS_AREA, PRODUCT_LINE, STRATEGY ) zz WHERE VAR05 <> 0 ORDER BY RANK, PL_REPORTING_REGION, BUSINESS_AREA, PRODUCT_LINE, STRATEGY ) SELECT y.RANK, y.PL_REPORTING_REGION, y.BUSINESS_AREA, y.PRODUCT_LINE, y.STRATEGY, x.COB_DATE, y.VAR05, y.DAILY_PNL, y.VaRBREACH FROM y RIGHT JOIN x ON y.RANK = x.RANK