SELECT p.cob_date as pnl_date, p.ccc_pl_reporting_region as pl_reporting_region, p.ccc_division as division, p.ccc_business_area as business_area, p.ccc_product_line as product_line, CASE WHEN p.risk_manager_id IS NULL THEN p.ccc_strategy || '.' ELSE p.ccc_strategy || '.' || p.risk_manager_id END AS strategy_trader, case when p.financial_element in ('AGENCY', 'MSCAP FEES', 'MSCI FEES', 'RISK') then 'Commissions' else 'Trading' end as Commissions, round(sum(p.daily_pl/1000000),2) as daily_pnl, round(sum(p.ytd_pl/1000000),2) as ytd_pnl FROM cdwuser.u_pct_pnl_current p WHERE p.ccc_division = 'INSTITUTIONAL EQUITY DIVISION' AND p.cob_date >= '2018-01-01' and p.cob_date <= '2018-02-28' AND NOT p.COB_DATE = '2017-01-03' AND (p.account_purpose_code <> 'J' or p.account_purpose_code is null) GROUP BY p.cob_date, p.ccc_pl_reporting_region, p.ccc_division, p.ccc_business_area, p.ccc_product_line, CASE WHEN p.risk_manager_id IS NULL THEN p.ccc_strategy || '.' ELSE p.ccc_strategy || '.' || p.risk_manager_id END, case when p.financial_element in ('AGENCY', 'MSCAP FEES', 'MSCI FEES', 'RISK') then 'Commissions' else 'Trading' end