SELECT     a.COB_DATE,     SUM(coalesce(CAST(a.USD_EQ_KAPPA AS DOUBLE PRECISION), 0)) AS USD_EQ_KAPPA,     SUM(coalesce(CAST(a.SLIDE_EQ_MIN_30_USD AS DOUBLE PRECISION), 0)) AS SLIDE_EQ_MIN_30_USD,     SUM(coalesce(CAST(a.SLIDE_EQ_MIN_10_USD AS DOUBLE PRECISION), 0)) AS SLIDE_EQ_MIN_10_USD FROM   cdwuser.U_EQ_MSR_INTRPLT a WHERE  A.CCC_PL_REPORTING_REGION in ('JAPAN','ASIA PACIFIC') AND A.CCC_TAPS_COMPANY in ('0302','0347','0853','4043','4298','4863','6120', '6899','6837','6893','4044','5869','0856','6325','0301','0893','0993') AND  (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-01-31') and     (a.ccc_business_area <> 'INTERNATIONAL WEALTH MGMT') AND     a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND     a.CCC_BANKING_TRADING = 'TRADING' AND          (a.VAR_EXCL_FL <> 'Y') GROUP BY     A.COB_DATE