SELECT CASE WHEN a.COB_DATE = '2018-02-28' THEN 'UK GROUP TOTAL TOTAL' WHEN a.COB_DATE = '2018-02-27' THEN 'UK GROUP TOTAL TOTAL Prev. CoB' END as Cut, COB_DATE, sum(DELTA) as DELTA, sum(abs(DELTA)) as GROSS_DELTA FROM (SELECT a.COB_DATE, a.ISSUE_ID_DECOMP, sum(coalesce(a.USD_EQ_DELTA_DECOMP,0))/1000 as Delta FROM CDWUSER.U_DECOMP_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-02-27') AND (a.CASH_ISSUE_TYPE <>'COMM' OR a.CASH_ISSUE_TYPE IS NULL) AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.LE_GROUP = 'UK' AND a.CCC_DIVISION <> 'FID DVA' AND a.CCC_DIVISION <> 'FIC DVA' AND CCC_STRATEGY <> 'MS DVA STR NOTES IED' group by a.COB_DATE, a.ISSUE_ID_DECOMP) a Group By COB_DATE UNION ALL SELECT Cut, COB_DATE, sum(DELTA) as DELTA, sum(Abs(Delta)) as GROSS_DELTA FROM (SELECT a.COB_DATE, a.ISSUE_ID_DECOMP, CASE WHEN a.COB_DATE = '2018-02-28' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('USA','CAN') THEN 'UK GROUP NAM TOTAL' WHEN a.COB_DATE = '2018-02-28' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK GROUP UK TOTAL' WHEN a.COB_DATE = '2018-02-28' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP EUROPE TOTAL' WHEN a.COB_DATE = '2018-02-28' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('JPN') THEN 'UK GROUP JAPAN TOTAL' WHEN a.COB_DATE = '2018-02-28' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('CHN') THEN 'UK GROUP CHINA TOTAL' WHEN a.COB_DATE = '2018-02-28' AND a.ISSUER_COUNTRY_CODE_DECOMP NOT IN ('USA','CAN','GBR','JPN','CHN','ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP OTHER TOTAL' WHEN a.COB_DATE = '2018-02-27' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('USA','CAN') THEN 'UK GROUP NAM TOTAL Prev. CoB' WHEN a.COB_DATE = '2018-02-27' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK GROUP UK TOTAL Prev. CoB' WHEN a.COB_DATE = '2018-02-27' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP EUROPE TOTAL Prev. CoB' WHEN a.COB_DATE = '2018-02-27' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('JPN') THEN 'UK GROUP JAPAN TOTAL Prev. CoB' WHEN a.COB_DATE = '2018-02-27' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('CHN') THEN 'UK GROUP CHINA TOTAL Prev. CoB' WHEN a.COB_DATE = '2018-02-27' AND a.ISSUER_COUNTRY_CODE_DECOMP NOT IN ('USA','CAN','GBR','JPN','CHN','ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP OTHER TOTAL Prev. CoB' END AS CUT, sum(coalesce(a.USD_EQ_DELTA_DECOMP,0))/1000 as Delta FROM CDWUSER.U_DECOMP_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-02-27') AND (a.CASH_ISSUE_TYPE <>'COMM' OR a.CASH_ISSUE_TYPE IS NULL) AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.LE_GROUP = 'UK' AND a.CCC_DIVISION <> 'FID DVA' AND a.CCC_DIVISION <> 'FIC DVA' AND CCC_STRATEGY <> 'MS DVA STR NOTES IED' GROUP BY a.COB_DATE, a.ISSUER_COUNTRY_CODE_DECOMP, a.ISSUE_ID_DECOMP) x GROUP BY COB_DATE, Cut