SELECT Cut, COB_DATE, sum(DELTA) as DELTA, sum(abs(DELTA)) as GROSS_DELTA FROM (SELECT a.COB_DATE, a.ISSUE_ID_DECOMP, CASE WHEN a.CCC_BUSINESS_AREA = 'CASH EQUITIES' THEN 'UK GROUP CASH EQUITIES TOTAL' WHEN a.CCC_BUSINESS_AREA = 'DERIVATIVES' THEN 'UK GROUP DERIVATIVES TOTAL' WHEN a.CCC_BUSINESS_AREA = 'OTHER IED' THEN 'UK GROUP OTHER IED TOTAL' WHEN a.CCC_BUSINESS_AREA = 'PRIME BROKERAGE' THEN 'UK GROUP PRIME BROKERAGE TOTAL' END AS Cut, sum(coalesce(a.USD_EQ_DELTA_DECOMP,0))/1000 as Delta FROM CDWUSER.U_DECOMP_MSR a WHERE a.COB_DATE = '2018-02-28' AND a.LE_GROUP = 'UK' AND (a.CASH_ISSUE_TYPE <>'COMM' OR a.CASH_ISSUE_TYPE IS NULL) AND (a.CCC_BUSINESS_AREA NOT IN ('CPM TRADING (MPE)', 'CPM','CREDIT', 'MS CVA MNE - FID', 'MS CVA MNE - COMMOD') AND a.CCC_STRATEGY NOT IN ('MS CVA MPE - DERIVATIVES', 'MS CVA MNE - DERIVATIVES', 'EQ XVA HEDGING')) AND a.CCC_DIVISION <> 'FID DVA' AND a.CCC_DIVISION <> 'FIC DVA' AND CCC_STRATEGY <> 'MS DVA STR NOTES IED' Group by a.COB_DATE, a.CCC_BUSINESS_AREA, a.ISSUE_ID_DECOMP) x Group By COB_DATE, Cut UNION ALL SELECT Cut, COB_DATE, sum(DELTA) as DELTA, sum(abs(DELTA)) as GROSS_DELTA FROM (SELECT a.COB_DATE, a.ISSUE_ID_DECOMP, CASE WHEN a.CCC_BUSINESS_AREA = 'CASH EQUITIES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('USA','CAN') THEN 'UK GROUP CASH EQUITIES NAM' WHEN a.CCC_BUSINESS_AREA = 'CASH EQUITIES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK GROUP CASH EQUITIES UK' WHEN a.CCC_BUSINESS_AREA = 'CASH EQUITIES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP CASH EQUITIES EUROPE' WHEN a.CCC_BUSINESS_AREA = 'CASH EQUITIES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('JPN') THEN 'UK GROUP CASH EQUITIES JAPAN' WHEN a.CCC_BUSINESS_AREA = 'CASH EQUITIES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('CHN') THEN 'UK GROUP CASH EQUITIES CHINA' WHEN a.CCC_BUSINESS_AREA = 'CASH EQUITIES' THEN 'UK GROUP CASH EQUITIES OTHER' WHEN a.CCC_BUSINESS_AREA = 'DERIVATIVES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('USA','CAN') THEN 'UK GROUP DERIVATIVES NAM' WHEN a.CCC_BUSINESS_AREA = 'DERIVATIVES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK GROUP DERIVATIVES UK' WHEN a.CCC_BUSINESS_AREA = 'DERIVATIVES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP DERIVATIVES EUROPE' WHEN a.CCC_BUSINESS_AREA = 'DERIVATIVES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('JPN') THEN 'UK GROUP DERIVATIVES JAPAN' WHEN a.CCC_BUSINESS_AREA = 'DERIVATIVES' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('CHN') THEN 'UK GROUP DERIVATIVES CHINA' WHEN a.CCC_BUSINESS_AREA = 'DERIVATIVES' THEN 'UK GROUP DERIVATIVES OTHER' WHEN a.CCC_BUSINESS_AREA = 'OTHER IED' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('USA','CAN') THEN 'UK GROUP OTHER IED NAM' WHEN a.CCC_BUSINESS_AREA = 'OTHER IED' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK GROUP OTHER IED UK' WHEN a.CCC_BUSINESS_AREA = 'OTHER IED' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP OTHER IED EUROPE' WHEN a.CCC_BUSINESS_AREA = 'OTHER IED' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('JPN') THEN 'UK GROUP OTHER IED JAPAN' WHEN a.CCC_BUSINESS_AREA = 'OTHER IED' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('CHN') THEN 'UK GROUP OTHER IED CHINA' WHEN a.CCC_BUSINESS_AREA = 'OTHER IED' THEN 'UK GROUP OTHER IED OTHER' WHEN a.CCC_BUSINESS_AREA = 'PRIME BROKERAGE' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('USA','CAN') THEN 'UK GROUP PRIME BROKERAGE NAM' WHEN a.CCC_BUSINESS_AREA = 'PRIME BROKERAGE' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('GBR') THEN 'UK GROUP PRIME BROKERAGE UK' WHEN a.CCC_BUSINESS_AREA = 'PRIME BROKERAGE' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'UK GROUP PRIME BROKERAGE EUROPE' WHEN a.CCC_BUSINESS_AREA = 'PRIME BROKERAGE' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('JPN') THEN 'UK GROUP PRIME BROKERAGE JAPAN' WHEN a.CCC_BUSINESS_AREA = 'PRIME BROKERAGE' AND a.ISSUER_COUNTRY_CODE_DECOMP IN ('CHN') THEN 'UK GROUP PRIME BROKERAGE CHINA' WHEN a.CCC_BUSINESS_AREA = 'PRIME BROKERAGE' THEN 'UK GROUP PRIME BROKERAGE OTHER' END AS CUT, sum(coalesce(a.USD_EQ_DELTA_DECOMP,0))/1000 as Delta FROM CDWUSER.U_DECOMP_MSR a WHERE a.COB_DATE = '2018-02-28' AND a.LE_GROUP = 'UK' AND (a.CASH_ISSUE_TYPE <>'COMM' OR a.CASH_ISSUE_TYPE IS NULL) AND (a.CCC_BUSINESS_AREA NOT IN ('CPM TRADING (MPE)', 'CPM','CREDIT', 'MS CVA MNE - FID', 'MS CVA MNE - COMMOD') AND a.CCC_STRATEGY NOT IN ('MS CVA MPE - DERIVATIVES', 'MS CVA MNE - DERIVATIVES', 'EQ XVA HEDGING')) AND a.CCC_DIVISION <> 'FID DVA' AND a.CCC_DIVISION <> 'FIC DVA' AND CCC_STRATEGY <> 'MS DVA STR NOTES IED' GROUP BY a.COB_DATE, a.CCC_BUSINESS_AREA, a.ISSUER_COUNTRY_CODE_DECOMP, a.ISSUE_ID_DECOMP) x GROUP BY Cut, COB_DATE