SELECT a.COB_DATE, a.PRODUCT_TYPE_CODE, a.LE_GROUP, a.TAPSCUSIP, a.PRODUCT_HIERARCHY_LEVEL7, a.curve_currency_pair, a.currency_pair, a.EXPIRATION_DATE, SUM (a.USD_NOTIONAL) AS NOTIONAL FROM cdwuser.U_OT_MSR a where cob_date in ('2018-02-28') AND a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND a.CCC_BUSINESS_AREA IN ('FXEM MACRO TRADING') AND a.CCC_BANKING_TRADING = 'TRADING' AND a.CURRENCY_PAIR LIKE '%AED%' AND a.USD_NOTIONAL <> 0 AND a.USD_NOTIONAL IS NOT NULL GROUP BY a.COB_DATE, a.PRODUCT_TYPE_CODE, a.PRODUCT_HIERARCHY_LEVEL7, a.LE_GROUP, a.TAPSCUSIP, a.curve_currency_pair, a.currency_pair, a.EXPIRATION_DATE