SELECT a.COB_DATE, a.ATTRIBUTE3, a.ATTRIBUTE4, case when (a.ATTRIBUTE9 like 'CR%' and not(a.ATTRIBUTE9 like '%Rotation%')) then 'CR Spot/Volatility' else a.ATTRIBUTE9 end as ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0))/1000000 as PNL FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.ANALYTICS = 'SCENARIOS' AND a.COB_DATE IN ('2018-02-16','2018-01-19') GROUP BY a.COB_DATE, a.ATTRIBUTE3, a.ATTRIBUTE4, case when (a.ATTRIBUTE9 like 'CR%' and not(a.ATTRIBUTE9 like '%Rotation%')) then 'CR Spot/Volatility' else a.ATTRIBUTE9 end, a.ATTRIBUTE10