SELECT * FROM ( SELECT '(Commod)' as DIVISION, a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0)) as VALUE1 FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.COB_DATE IN ('2018-02-16','2018-01-19') AND a.ATTRIBUTE1 = 'CM' AND a.ATTRIBUTE3 = 'COMMODITIES' AND ANALYTICS = 'SENSITIVITIES' GROUP BY a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10 UNION ALL SELECT '(Others)' as DIVISION, a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0)) as VALUE1 FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.COB_DATE IN ('2018-02-16','2018-01-19') AND a.ATTRIBUTE1 = 'CM' AND a.ATTRIBUTE3 <> 'COMMODITIES' AND a.ATTRIBUTE3 <> 'INSTITUTIONAL EQUITY DIVISION' AND ANALYTICS = 'SENSITIVITIES' GROUP BY a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10 UNION ALL SELECT '(IED)' as DIVISION, a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0)) as VALUE1 FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.COB_DATE IN ('2018-02-16','2018-01-19') AND a.ATTRIBUTE1 = 'CM' AND a.ATTRIBUTE3 = 'INSTITUTIONAL EQUITY DIVISION' AND ANALYTICS = 'SENSITIVITIES' GROUP BY a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10 UNION ALL SELECT '(ISG)' as DIVISION, a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0)) as VALUE1 FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.COB_DATE IN ('2018-02-16','2018-01-19') AND a.ATTRIBUTE1 = 'CM' AND ANALYTICS = 'SENSITIVITIES' GROUP BY a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10 ) x WHERE ATTRIBUTE9 in ('CM Delta','CM Skew','CM Smile','CM Vanna','CM Vega')