SELECT * FROM ( SELECT '(FID)' as DIVISION, a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0)) as VALUE1 FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.COB_DATE IN ('2018-02-16','2018-01-19') AND a.ATTRIBUTE1 = 'CR' AND a.ATTRIBUTE3 = 'FIXED INCOME DIVISION' AND ANALYTICS = 'SENSITIVITIES' GROUP BY a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10 UNION ALL SELECT '(OTHERS)' as DIVISION, a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0)) as VALUE1 FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.COB_DATE IN ('2018-02-16','2018-01-19') AND a.ATTRIBUTE1 = 'CR' AND a.ATTRIBUTE3 <> 'FIXED INCOME DIVISION' AND ANALYTICS = 'SENSITIVITIES' GROUP BY a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10 UNION ALL SELECT '(ISG)' as DIVISION, a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10, sum(coalesce(a.VALUE1,0)) as VALUE1 FROM CDWUSER.U_GENERIC_DATA a WHERE a.ANALYTIC_GROUP = 'FDSF' AND a.COB_DATE IN ('2018-02-16','2018-01-19') AND a.ATTRIBUTE1 = 'CR' AND ANALYTICS = 'SENSITIVITIES' GROUP BY a.COB_DATE, a.ATTRIBUTE9, a.ATTRIBUTE10 ) x WHERE ATTRIBUTE9 in ('CR Vega','CR Recovery','CR Notional','CR Market Value','CR JTD','CR Delta','CR Base Corr')