SELECT     a.COB_DATE,     a.CURRENCY_OF_MEASURE,     SUM (a.USD_FX) AS USD_FX FROM cdwuser.U_FX_MSR a WHERE     1 = 1 AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-01-31') and     ( A.CCC_PL_REPORTING_REGION in ('JAPAN','ASIA PACIFIC') AND A.CCC_TAPS_COMPANY in ('0302','0347','0853','4043','4298','4863','6120', '6899','6837','6893','4044','5869','0856','6325','0301','0893','0993') AND      1=1) AND     ((a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND       a.CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND       a.CCC_PRODUCT_LINE = 'DISTRESSED TRADING') OR      a.CCC_DIVISION = 'INSTITUTIONAL SECURITIES OTHER') AND     (a.VAR_EXCL_FL <> 'Y') GROUP BY     a.COB_DATE,     a.CURRENCY_OF_MEASURE