select sum(a.USD_IR_UNIFIED_PV01) as PV01, case when product_type_code not in ('SWAP','FX','GVTBOND') then 'other' else product_type_code end as prodgroup, a.ccc_business_area,a.CURRENCY_OF_MEASURE, a.COB_DATE,a.CCC_PL_REPORTING_REGION, CASE WHEN a.CURRENCY_OF_MEASURE in ('PLN') then 'PLN' WHEN a.CURRENCY_OF_MEASURE in ('CZK') then 'CZK' WHEN a.CURRENCY_OF_MEASURE in ('ZAR') then 'ZAR' WHEN a.CURRENCY_OF_MEASURE in ('RUB','RBX') then 'RUB' WHEN a.CURRENCY_OF_MEASURE in ('TRY') then 'TRY' WHEN a.CURRENCY_OF_MEASURE in ('HUF') then 'HUF' ELSE 'EM' end as GROUP_CCY, CASE WHEN a.term_NEW <= 0.25 then '03M' WHEN a.term_NEW <= 0.5 then '06M' WHEN a.term_NEW <= 1 then '01Y' WHEN a.term_NEW <= 2 then '02Y' WHEN a.term_NEW <= 5 then '05Y' WHEN a.term_NEW <= 7 then '07Y' WHEN a.term_NEW <= 10 then '10Y' else '20Y' end as GROUP_TENOR from cdwuser.U_IR_MSR_INTRPLT a where a.COB_DATE in ('2018-02-28') AND ccc_business_area IN ('FXEM MACRO TRADING','EM CREDIT TRADING') AND CCC_PL_REPORTING_REGION in ('EMEA') AND a.CURRENCY_OF_MEASURE not in ('DKK','NOK','SEK','CAD','NZD','AUD','JPY','CHF','GBP','EUR','USD','UBD') AND USD_IR_UNIFIED_PV01 is not NULL AND USD_IR_UNIFIED_PV01 <>0 group by a.ccc_business_area,a.COB_DATE,a.CCC_PL_REPORTING_REGION,a.CURRENCY_OF_MEASURE, CASE WHEN a.term_NEW <= 0.25 then '03M' WHEN a.term_NEW <= 0.5 then '06M' WHEN a.term_NEW <= 1 then '01Y' WHEN a.term_NEW <= 2 then '02Y' WHEN a.term_NEW <= 5 then '05Y' WHEN a.term_NEW <= 7 then '07Y' WHEN a.term_NEW <= 10 then '10Y' else '20Y' end, case when product_type_code not in ('SWAP','FX','GVTBOND') then 'other' else product_type_code end