select a.COB_DATE, a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME, sum(a.USD_PV10_BENCH) as PV10, SUM (a.USD_EXPOSURE) AS EXPOSURE from cdwuser.U_CR_MSR a where a.COB_DATE in ('2018-02-28', '2018-02-21') AND a.CCC_BUSINESS_AREA in ('FXEM MACRO TRADING','EM CREDIT TRADING') AND CCC_PL_REPORTING_REGION = 'EMEA' and issuer_country_code not in ('UKR','ARG','VEN') and CCC_BANKING_TRADING = 'TRADING' group by a.COB_DATE, a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME