select sum(a.USD_PV10_BENCH) as PV10, a.issuer_country_em_flag, a.ccc_business_area, a.COB_DATE, a.CCC_PL_REPORTING_REGION, CASE when a.ISSUER_COUNTRY_CODE in ('RUS') then 'RUSSIA' when a.ISSUER_COUNTRY_CODE in ('ZAF') then 'SOUTH-AFRICA' when a.ISSUER_COUNTRY_CODE in ('TUR') then 'TURKEY' when a.ISSUER_COUNTRY_CODE in ('ARE','IRQ','QAT','SAU','LBN','ISR','EGY','ISR','KWT','BHR','OMN','JOR') then 'MID-EAST' WHEN a.ISSUER_COUNTRY_CODE IN ('EGY','MAR','GAB','GHA','AGO','MNE','MOZ','TZA','ETH','ETH','CIV','KEN','SEN','NGA','TUN','ZMB','NAM','RWA') then 'AFRICA' WHEN a.ISSUER_COUNTRY_CODE IN ('BGR','HRV','BIH','CZE','EST','HUN','ISL','LVA','POL','ROM','SRB','SVK','SVN','LBR','LTU','ROU','KAZ','AZE','BLR','GEO','SLV','ARM','UKR') THEN 'EAST-EUROPE' WHEN a.ISSUER_COUNTRY_CODE IN ('BRA','CHL','COL','DOM','ECU','JAM','MEX','PAN','PER','URY','ARG') THEN 'LATAM' else 'Other' end as country_group, case when PRODUCT_TYPE_CODE in ('BOND') then 'BOND' when PRODUCT_TYPE_CODE in ('CRDINDEX') then 'CRDINDEX' when PRODUCT_TYPE_CODE in ('DEFSWAP') then 'DEFSWAP' else 'OTHER' end as PRODUCT_GROUP from cdwuser.U_DM_CC a where a.COB_DATE >= '2017-12-30' AND USD_PV10_BENCH is not NULL AND USD_PV10_BENCH <>0 AND ccc_business_area IN ('FXEM MACRO TRADING','EM CREDIT TRADING') AND CCC_PL_REPORTING_REGION in ('EMEA') group by a.issuer_country_em_flag, a.ccc_business_area, a.COB_DATE,a.CCC_PL_REPORTING_REGION, CASE when a.ISSUER_COUNTRY_CODE in ('RUS') then 'RUSSIA' when a.ISSUER_COUNTRY_CODE in ('ZAF') then 'SOUTH-AFRICA' when a.ISSUER_COUNTRY_CODE in ('TUR') then 'TURKEY' when a.ISSUER_COUNTRY_CODE in ('ARE','IRQ','QAT','SAU','LBN','ISR','EGY','ISR','KWT','BHR','OMN','JOR') then 'MID-EAST' WHEN a.ISSUER_COUNTRY_CODE IN ('EGY','MAR','GAB','GHA','AGO','MNE','MOZ','TZA','ETH','ETH','CIV','KEN','SEN','NGA','TUN','ZMB','NAM','RWA') then 'AFRICA' WHEN a.ISSUER_COUNTRY_CODE IN ('BGR','HRV','BIH','CZE','EST','HUN','ISL','LVA','POL','ROM','SRB','SVK','SVN','LBR','LTU','ROU','KAZ','AZE','BLR','GEO','SLV','ARM','UKR') THEN 'EAST-EUROPE' WHEN a.ISSUER_COUNTRY_CODE IN ('BRA','CHL','COL','DOM','ECU','JAM','MEX','PAN','PER','URY','ARG') THEN 'LATAM' else 'Other' end, case when PRODUCT_TYPE_CODE in ('BOND') then 'BOND' when PRODUCT_TYPE_CODE in ('CRDINDEX') then 'CRDINDEX' when PRODUCT_TYPE_CODE in ('DEFSWAP') then 'DEFSWAP' else 'OTHER' end