SELECT CASE WHEN (CCC_PL_REPORTING_REGION IN ('EMEA') AND CURRENCY_OF_MEASURE IN ('RUB', 'RU1', 'RBX')) THEN 'RUB' WHEN (CURRENCY_OF_MEASURE IN ('CNY', 'CNH') AND ccc_taps_company = '0302') THEN 'CNY' WHEN (CURRENCY_OF_MEASURE IN ('HKD') AND ccc_taps_company = '0302') THEN 'HKD' WHEN (CURRENCY_OF_MEASURE IN ('IDR', 'INR', 'KRW', 'MYR', 'LKR', 'PHP', 'SGD', 'THB', 'TWD', 'VND', 'KR1', 'KRX') AND ccc_taps_company = '0302') THEN 'OTH' END AS currency, cob_date, SUM (usd_fx) AS FX FROM cdwuser.U_DM_FX WHERE COB_DATE >= '2017-08-28' AND ccc_business_area IN ('FXEM MACRO TRADING', 'EM CREDIT TRADING') AND CURRENCY_OF_MEASURE IN ('RUB', 'RU1', 'RBX', 'CNH', 'CNY', 'HKD', 'IDR', 'INR', 'KRW', 'MYR', 'LKR', 'PHP', 'SGD', 'THB', 'TWD', 'VND', 'KR1', 'KRX') GROUP BY cob_date, CASE WHEN (CCC_PL_REPORTING_REGION IN ('EMEA') AND CURRENCY_OF_MEASURE IN ('RUB', 'RU1', 'RBX')) THEN 'RUB' WHEN (CURRENCY_OF_MEASURE IN ('CNY', 'CNH') AND ccc_taps_company = '0302') THEN 'CNY' WHEN (CURRENCY_OF_MEASURE IN ('HKD') AND ccc_taps_company = '0302') THEN 'HKD' WHEN (CURRENCY_OF_MEASURE IN ('IDR', 'INR', 'KRW', 'MYR', 'LKR', 'PHP', 'SGD', 'THB', 'TWD', 'VND', 'KR1', 'KRX') AND ccc_taps_company = '0302') THEN 'OTH' END