select A.COB_DATE, a.CURRENCY_OF_MEASURE, a.CURVE_NAME,a.POSITION_ISSUER_PARTY_DARWIN_NAME, Case when A.TERM_OF_MEASURE/365<3 then '1-3yr' when A.TERM_OF_MEASURE/365>=3 and A.TERM_OF_MEASURE/365<7 then '3-7yr'  when A.TERM_OF_MEASURE/365>=7 and A.TERM_OF_MEASURE/365<12 then '7-12yr' when A.TERM_OF_MEASURE/365>=12 and A.TERM_OF_MEASURE/365<20 then '12-20yr'  when A.TERM_OF_MEASURE/365>=20 then '20yr' Else 'other' end as TERM, CASE WHEN a.CCC_DIVISION IN('FIXED INCOME DIVISION','INSTITUTIONAL EQUITY DIVISION') THEN a.CCC_DIVISION ELSE 'OTHER' END AS CCC_DIVISION, CASE WHEN CCC_TAPS_COMPANY IN('7325','5972') THEN 'Caieiras Fundo de Investimento (5972, 7325)'  WHEN CCC_TAPS_COMPANY IN('4171','0921','6411') THEN 'Banco Morgan Stanley S.A. (0921, 4171, 6411)'  WHEN CCC_TAPS_COMPANY='0925' THEN  'Morgan Stanley Corretora de Titulos e Valores Mobiliarios (0925)'  WHEN CCC_TAPS_COMPANY IN('8890','8840','4171','0921','0925','5972','7325','6411') THEN CCC_TAPS_COMPANY ELSE 'OTHERS' END AS LEGAL_ENTITY, CASE WHEN A.PRODUCT_TYPE_CODE IN('CAP','FLOOR','SWAPTION') THEN 'OPTION' ELSE A.PRODUCT_TYPE_CODE END AS PRODUCT_TYPE_CODE, sum(a.USD_IR_UNIFIED_PV01) as PV01 from cdwuser.U_IR_MSR a WHERE cob_date in ('2018-02-28','2018-02-27') AND A.CCC_TAPS_COMPANY IN ('8890','8840') and a.book ='LMXDA' and a.VAR_EXCL_FL='N' AND A.USD_IR_UNIFIED_PV01 IS NOT NULL GROUP BY a.CURRENCY_OF_MEASURE, a.CURVE_NAME,a.CCC_DIVISION,A.CCC_TAPS_COMPANY,A.COB_DATE,A.TERM_OF_MEASURE,a.POSITION_ISSUER_PARTY_DARWIN_NAME, CASE WHEN A.PRODUCT_TYPE_CODE IN('CAP','FLOOR','SWAPTION') THEN 'OPTION' ELSE A.PRODUCT_TYPE_CODE END