SELECT a.CURRENCY_OF_MEASURE         , sum(a.USD_FX) AS Delta         , a.COB_DATE         , CASE WHEN A.PRODUCT_TYPE_CODE IN (                                 'CAP'                                 , 'FLOOR'                                 , 'SWAPTION'                                 ) THEN 'OPTION' ELSE A.PRODUCT_TYPE_CODE END AS PRODUCT_TYPE_CODE         , CASE WHEN a.CCC_DIVISION IN (                                 'FIXED INCOME DIVISION'                                 , 'INSTITUTIONAL EQUITY DIVISION'                                 ) THEN a.CCC_DIVISION ELSE 'OTHER' END AS CCC_DIVISION         , CASE WHEN CCC_TAPS_COMPANY IN (                                 '7325'                                 , '5972'                                 ) THEN 'Caieiras Fundo de Investimento (5972, 7325)' WHEN CCC_TAPS_COMPANY IN (                                 '4171'                                 , '0921'                                 , '6411'                                 ) THEN 'Banco Morgan Stanley S.A. (0921, 4171, 6411)' WHEN CCC_TAPS_COMPANY = '0925' THEN 'Morgan Stanley Corretora de Titulos e Valores Mobiliarios (0925)' WHEN CCC_TAPS_COMPANY IN (                                 '8890'                                 , '8840'                                 , '4171'                                 , '0921'                                 , '0925'                                 , '5972'                                 , '7325'                                 , '6411'                                 ) THEN CCC_TAPS_COMPANY ELSE 'OTHERS' END AS LEGAL_ENTITY FROM cdwuser.u_fx_msr a WHERE 1=1     AND cob_date >= '2018-01-31'     AND cob_date <= '2018-02-28'         AND a.CURRENCY_OF_MEASURE IN (                 'MXN'                 , 'CAD'                 , 'EUR'                 , 'USD'                 , 'JPY'                 , 'AUD'                 )         AND a.VAR_EXCL_FL = 'N' GROUP BY a.CURRENCY_OF_MEASURE         , a.CCC_DIVISION         , A.CCC_TAPS_COMPANY         , A.COB_DATE         , a.PRODUCT_TYPE_CODE         , CASE WHEN A.PRODUCT_TYPE_CODE IN (                                 'CAP'                                 , 'FLOOR'                                 , 'SWAPTION'                                 ) THEN 'OPTION' ELSE A.PRODUCT_TYPE_CODE END