SELECT      A.COB_DATE,     A.CCC_DIVISION,     A.CURVE_CURRENCY,     CASE          WHEN A.TERM_OF_MEASURE / 365 < 3 THEN '1-3YR'         WHEN A.TERM_OF_MEASURE / 365 >= 3 AND A.TERM_OF_MEASURE / 365 < 7 THEN '3-7YR'          WHEN A.TERM_OF_MEASURE / 365 >= 7 AND A.TERM_OF_MEASURE / 365 < 12 THEN '7-12YR'         WHEN A.TERM_OF_MEASURE / 365 >= 12 AND A.TERM_OF_MEASURE / 365 < 20 THEN '12-20YR'         WHEN A.TERM_OF_MEASURE / 365 >= 20 THEN '20YR'         ELSE 'OTHER'      END      AS TERM,     A.CURRENCY_OF_MEASURE,     SUM(A.USD_IR_UNIFIED_PV01) AS USD_IR_UNIFIED_PV01 FROM      cdwuser.U_QIS_EXP_MSR A WHERE      1 = 1 AND      A.COB_DATE >= '2018-01-31' AND     A.COB_DATE <= '2018-02-28' AND     A.QIS_RUN_TYPE = 'MEXICO_LOCALVAR' GROUP BY     A.COB_DATE,     A.CCC_DIVISION,     A.CURVE_CURRENCY,     CASE          WHEN A.TERM_OF_MEASURE / 365 < 3 THEN '1-3YR'         WHEN A.TERM_OF_MEASURE / 365 >= 3 AND A.TERM_OF_MEASURE / 365 < 7 THEN '3-7YR'          WHEN A.TERM_OF_MEASURE / 365 >= 7 AND A.TERM_OF_MEASURE / 365 < 12 THEN '7-12YR'         WHEN A.TERM_OF_MEASURE / 365 >= 12 AND A.TERM_OF_MEASURE / 365 < 20 THEN '12-20YR'         WHEN A.TERM_OF_MEASURE / 365 >= 20 THEN '20YR'         ELSE 'OTHER'      END,     A.CURRENCY_OF_MEASURE