SELECT v.COB_DATE, ccc_pl_reporting_region, CASE WHEN LE_GROUP = 'UK' THEN 'UK Group' ELSE 'Non Uk Group' END AS UK_Group, SUM (v.SLIDE_EQ_MIN_20_USD) AS D20raw FROM CDWUSER.U_EQ_MSR v WHERE COB_DATE in ('2018-02-28','2018-02-21') AND v.SILO_SRC = 'IED' AND v.DIVISION = 'IED' AND v.CCC_BANKING_TRADING = 'TRADING' GROUP BY v.COB_DATE, ccc_pl_reporting_region, CASE WHEN LE_GROUP = 'UK' THEN 'UK Group' ELSE 'Non Uk Group' END