SELECT     CURRENCY_PAIR,     SUM (PNL) AS PNL FROM     (         SELECT             source_scn_name,            CURRENCY_PAIR,             SUM (raw_pnl) / 1000 PNL         FROM DWUSER.U_RAW_SCENARIO_PNL a         WHERE COB_DATE = '2018-02-28' AND             CCC_BUSINESS_AREA = 'FXEM MACRO TRADING' AND             PRODUCT_TYPE IN ('FXOPT') AND             source_scn_name = 'BHC2017_S1_FX_ALL' AND             BU_RISK_SYSTEM LIKE 'FXOPT%'         GROUP BY             source_scn_name             ,CURRENCY_PAIR     )     a GROUP BY CURRENCY_PAIR