SELECT SUM (USD_FX) AS FX, cob_date, currency_of_measure, ccc_business_area, a.ccc_division FROM cdwuser.U_DM_FX a WHERE cob_date in ('2018-02-28', '2018-02-27') and currency_of_measure in ('HUF') AND a.ccc_division IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION') AND usd_fx <> 0 AND ccc_banking_trading_localreg = 'TRADING' AND ccc_business_area NOT IN ('CPM', 'DSP - CREDIT', 'LENDING', 'OTC CLIENT CLEARING') GROUP BY cob_date, currency_of_measure, ccc_business_area, a.ccc_division