SELECT a.COB_DATE, a.ccc_division, SUM (a.USD_PV10_BENCH) AS PV10 FROM cdwuser.U_DM_CC a WHERE a.COB_DATE >= '2014-12-31' and a.ISSUER_COUNTRY_CODE = 'HUN' AND USD_PV10_BENCH IS NOT NULL AND USD_PV10_BENCH <> 0 AND a.ccc_business_area NOT IN ('CPM', 'LENDING', 'OTC CLIENT CLEARING') AND a.ccc_division IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION') AND a.CCC_BANKING_TRADING_LOCALREG = 'TRADING' GROUP BY a.COB_DATE, a.ccc_division