SELECT COB_DATE, DIVISION, SUM (DELTA) AS DELTA, SUM (Abs_Delta) AS GROSS_DELTA FROM ( SELECT d.COB_DATE, d.DIVISION, d.ISSUE_ID_DECOMP, SUM (COALESCE (d.USD_EQ_DELTA_DECOMP,0)) AS Delta, ABS (SUM (COALESCE (d.USD_EQ_DELTA_DECOMP,0))) AS Abs_Delta FROM cdwuser.U_DECOMP_MSR d WHERE d.cob_date in ('2018-02-28', '2018-02-27') and ISSUER_COUNTRY_CODE_DECOMP = 'HUN' AND d.CCC_BANKING_TRADING = 'TRADING' AND d.division IN ('FID', 'IED') AND (d.CASH_ISSUE_TYPE <> 'COMM' OR d.CASH_ISSUE_TYPE IS NULL) GROUP BY d.COB_DATE, d.DIVISION, d.ISSUE_ID_DECOMP ) x GROUP BY COB_DATE, DIVISION