SELECT a.COB_DATE, a.ccc_division, SUM (a.USD_IR_UNIFIED_PV01) AS PV01 FROM cdwuser.U_DM_IR a WHERE a.COB_DATE >= '2014-12-31' and currency_of_measure in ('ILS') AND USD_IR_UNIFIED_PV01 IS NOT NULL AND USD_IR_UNIFIED_PV01 <> 0 AND a.ccc_business_area NOT IN ('CPM', 'LENDING', 'OTC CLIENT CLEARING') AND a.ccc_division IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION') AND a.CCC_BANKING_TRADING_LOCALREG = 'TRADING' GROUP BY a.COB_DATE, a.ccc_division