SELECT cob_date, CURRENCY_OF_MEASURE, case when TERM_NEW <=5 then '0-5y' else '5y+' end as term, sum(coalesce(a.usd_pca1,0)) as PCA1, a.CCC_STRATEGY FROM CDWUSER.U_DM_IR a WHERE a.COB_DATE in ('2018-02-28','2018-02-21') AND CURRENCY_OF_MEASURE IN ('EUR', 'GBP','USD') AND CCC_BUSINESS_AREA IN ('STRUCTURED RATES','LIQUID FLOW RATES') AND CCC_PL_REPORTING_REGION = 'EMEA' AND BOOK NOT IN ('TOSPL') AND usd_pca1 is not null AND usd_pca1 <> 0 GROUP BY cob_date , CURRENCY_OF_MEASURE, case when TERM_NEW <=5 then '0-5y' else '5y+' end, a.CCC_STRATEGY