SELECT sum (USD_IR_UNIFIED_PV01) as usd_pv01, sum(a.total_breakeven_pv01) as inflation_pv01, sum(usd_ir_kappa)/10 as usd_kappa, cob_date, CASE WHEN CURRENCY_OF_MEASURE IN ('EUR', 'GBP','USD') THEN CURRENCY_OF_MEASURE ELSE 'Others' END AS CURRENCY_OF_MEASURE1, a.CCC_STRATEGY FROM CDWUSER.U_DM_IR a WHERE a.COB_DATE >= '2017-11-30' AND CCC_BUSINESS_AREA IN ('STRUCTURED RATES','LIQUID FLOW RATES') AND CCC_PL_REPORTING_REGION = 'EMEA' AND VAR_EXCL_FL = 'N' AND VERTICAL_SYSTEM NOT IN ('PIPELINE_NY') AND ccc_product_line NOT LIKE 'MS CVA MPE%' AND ccc_business_area NOT LIKE 'CPM TRADING%' AND (ccc_product_line NOT LIKE 'NON IG PRIMARY%' OR ccc_strategy NOT LIKE 'NON IG PRIMARY%') AND (ccc_product_line NOT IN ('MS CVA MPE DERIVS CPM', 'CORE MPE CVA', 'MONOLINE MPE CVA', 'MS CVA MNE DERIVS IRCC') OR ccc_strategy NOT IN ('MS CVA MPE DERIVS CPM', 'CORE MPE CVA', 'MONOLINE MPE CVA', 'MS CVA MNE DERIVS IRCC')) AND CCC_BUSINESS_AREA NOT IN ('LENDING') AND CCC_BUSINESS_AREA NOT LIKE '%CVA%' GROUP BY cob_date, CASE WHEN CURRENCY_OF_MEASURE IN ('EUR', 'GBP','USD') THEN CURRENCY_OF_MEASURE ELSE 'Others' END, a.CCC_STRATEGY