SELECT a.COB_DATE, A.ACCOUNT, A.BOOK, A.CCC_TAPS_COMPANY, a.CCC_PRODUCT_LINE, A.CCC_STRATEGY, A.SECTYPE2, A.CCC_HIERARCHY_LEVEL0, A.POSITION_CHILD_ISSUER_PARTY_DARWIN_NAME, a.POSITION_CHILD_ISSUER_PARTY_DARWIN_ID, SUM (CAST(A.USD_PV01SPRD as numeric(15,5))) AS SPRD_PV01, SUM (CAST(A.USD_IR_UNIFIED_PV01 as numeric(15,5))) AS PV01, SUM (COALESCE (CAST(A.USD_NOTIONAL as numeric(15,5)), 0)) AS usd_notional, SUM (COALESCE (CAST(A.USD_MARKET_VALUE as numeric(15,5)), 0)) AS usd_market_value, SUM (CAST(A.USD_DELTA as numeric(15,5))) AS EQUITY_DELTA, SUM (COALESCE (CAST(USD_IR_UNIFIED_PV01 as numeric(15,5)), 0)) AS USD_PV01, SUM (COALESCE (CAST(a.USD_EXPOSURE as numeric(15,5)), 0)) AS usd_net_expousre, SUM (COALESCE (CAST(a.USD_UNFUNDED_COMMIT as numeric(15,5)), 0)) AS USD_UNFUNDED_COMMIT, SUM (COALESCE (CAST(a.USD_FUNDED_COMMIT as numeric(15,5)), 0)) AS USD_FUNDED_COMMIT, SUM (COALESCE ( CAST(a.USD_COMMITTMENT as numeric(15,5)), 0)) AS USD_COMMIt FROM Cdwuser.u_EXP_msr a WHERE A.COB_DATE IN ( '2018-02-28', '2018-02-27', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31', '2017-09-29', '2017-08-31' ) AND a.BOOK NOT IN ('BKCRA') and a.CCC_STRATEGY IN ('CRA LOANS1') GROUP BY a.COB_DATE, A.ACCOUNT, A.BOOK, A.CCC_TAPS_COMPANY, a.CCC_PRODUCT_LINE, A.CCC_STRATEGY, A.SECTYPE2, A.CCC_HIERARCHY_LEVEL0, A.POSITION_CHILD_ISSUER_PARTY_DARWIN_NAME, a.POSITION_CHILD_ISSUER_PARTY_DARWIN_ID