SELECT STRESS_SCENARIO, SUM(SCENARIO_PNL) AS PNL  FROM DWUSER.u_modular_scenarios WHERE COB_DATE = '2018-01-31' AND SCENARIO_TYPE = 'FULL_REVAL' AND position_currency = 'GBP' AND ccc_business_area in ('STRUCTURED RATES')  and ccc_strategy in ('INFLATION PRODUCTS EU' ,'INFLATION PRODUCTS JP','INFLATION PRODUCTS NA') and RUN_PROFILE IN ('STS_MOD_SCN_RUN_NY', 'STS_MOD_SCN_RUN_LN', 'STS_MOD_SCN_RUN_ASIA')  and (STRESS_SCENARIO like 'RSCN INF ALL PARALLEL%'  or STRESS_SCENARIO like 'RSCN INFVOL ALL PARALLEL%'  or STRESS_SCENARIO like 'RSCN CROSSINF_INFVO%' )   Group BY  STRESS_SCENARIO