SELECT COB_DATE, CASE WHEN PRODUCT_TYPE_CODE in ('GVTBOND','BONDFUT') THEN PRODUCT_TYPE_CODE WHEN PRODUCT_HIERARCHY_LEVEL7 IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE') THEN 'Covered Bonds' WHEN (PRODUCT_TYPE_CODE in ('BOND','AGN') AND PRODUCT_HIERARCHY_LEVEL7 NOT IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE')) THEN 'Supranationals and Agencies' END AS Classification, case when round(TERM_OF_MEASURE/365,0) <=1 then '0-1yr'
when round(TERM_OF_MEASURE/365,0) <=5 then '2-5yr'
when round(TERM_OF_MEASURE/365,0) <=12 then '7-12yr'
when round(TERM_OF_MEASURE/365,0) <=30 then '15-30yr'
else '40yr+' end as term_group, case when COUNTRY_CD_OF_RISK in ('DEU','FRA','BEL','ITA','ESP','IRL','SWE','USA') then COUNTRY_CD_OF_RISK else 'Others' end as country_code, SUM (coalesce(USD_IR_UNIFIED_PV01,0)) AS country_pv01, SUM (coalesce(USD_EXPOSURE,0)) AS country_net_exposure FROM CDWUSER.U_EXP_MSR WHERE COB_DATE IN ('2018-02-28','2018-02-27') AND CCC_PL_REPORTING_REGION = 'EMEA' AND CCC_BUSINESS_AREA = 'LIQUID FLOW RATES' GROUP BY COB_DATE, CASE WHEN PRODUCT_TYPE_CODE in ('GVTBOND','BONDFUT') THEN PRODUCT_TYPE_CODE WHEN PRODUCT_HIERARCHY_LEVEL7 IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE') THEN 'Covered Bonds' WHEN (PRODUCT_TYPE_CODE in ('BOND','AGN') AND PRODUCT_HIERARCHY_LEVEL7 NOT IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE')) THEN 'Supranationals and Agencies' END, case when round(TERM_OF_MEASURE/365,0) <=1 then '0-1yr'
when round(TERM_OF_MEASURE/365,0) <=5 then '2-5yr'
when round(TERM_OF_MEASURE/365,0) <=12 then '7-12yr'
when round(TERM_OF_MEASURE/365,0) <=30 then '15-30yr'
else '40yr+' end, case when COUNTRY_CD_OF_RISK in ('DEU','FRA','BEL','ITA','ESP','IRL','SWE','USA') then COUNTRY_CD_OF_RISK else 'Others' end