SELECT COB_DATE, CCC_BUSINESS_AREA, BOOK, CASE WHEN COUNTRY_CD_OF_RISK = 'ITA' THEN 'ITALY' WHEN COUNTRY_CD_OF_RISK = 'ESP' THEN 'SPAIN' WHEN COUNTRY_CD_OF_RISK = 'PRT' THEN 'PORTUGAL' WHEN COUNTRY_CD_OF_RISK = 'GRC' THEN 'GREECE' WHEN COUNTRY_CD_OF_RISK IN ('CYP','EST','LVA','LTU','MLT','SVK','SVN') THEN 'Other' END AS COUNTRY_CD_OF_RISK, CASE WHEN TERM_NEW <=5 THEN '0-5y' WHEN TERM_NEW > 5 AND TERM_NEW <= 10 THEN '5-10y' WHEN TERM_NEW > 10 THEN '10y+' END AS TERM_NEW, CASE WHEN PRODUCT_TYPE_CODE in ('GVTBOND','GVTBONDIL','BONDFUT','GOVTBONDOPT') THEN 'Government Bonds' WHEN (PRODUCT_TYPE_CODE in ('BOND','AGN') AND PRODUCT_HIERARCHY_LEVEL7 IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE','UNKNOWN')) THEN 'Covered Bonds' WHEN (PRODUCT_TYPE_CODE in ('BOND','AGN') AND PRODUCT_HIERARCHY_LEVEL7 NOT IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE','UNKNOWN')) THEN 'Supranationals and Agencies' WHEN PRODUCT_TYPE_CODE in ('CRDBSKT','CRDINDEX','DEFSWAP') THEN 'Credit Products' WHEN PRODUCT_TYPE_CODE in ('ABS','RMBS','RMBS_ARM','RMBS_FIX') THEN 'ABS/RMBS' WHEN PRODUCT_TYPE_CODE NOT IN ('GVTBOND','GVTBONDIL','BONDFUT','GOVTBONDOPT','BOND','AGN','CRDBSKT','CRDINDEX','DEFSWAP','ABS','RMBS_ARM') THEN 'Other' END AS SecType, PRODUCT_HIERARCHY_LEVEL7, CCC_PRODUCT_LINE, PRODUCT_TYPE_CODE, SECTYPE_GROUP, SUM (USD_EXPOSURE) AS USD_EXPOSURE FROM CDWUSER.U_DM_IR WHERE cob_date IN ('2018-02-28', '2018-02-27') AND CCC_BUSINESS_AREA = 'LIQUID FLOW RATES' AND CCC_PL_REPORTING_REGION = 'EMEA' AND CCC_division IN ('FIXED INCOME DIVISION') AND USD_EXPOSURE IS NOT NULL AND USD_EXPOSURE <> 0 AND CCC_BUSINESS_AREA NOT IN ('LENDING') AND CCC_BUSINESS_AREA NOT LIKE '%CVA%' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%' AND (ccc_strategy NOT IN ('CREL BANK HFI/HFS', 'CREL SECURITIZATION') OR PRODUCT_TYPE_CODE NOT IN ('LOAN')) AND CCC_BUSINESS_AREA NOT IN ('DSP - CREDIT') AND COUNTRY_CD_OF_RISK IN ('ESP', 'ITA', 'GRC', 'PRT','CYP','EST','LVA','LTU','MLT','SVK','SVN') AND PRODUCT_TYPE_CODE NOT IN ('WAREHOUSE') AND NOT (CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND (CCC_PRODUCT_LINE IN ('NON INVSMT GRADE PRIMARY', 'NON IG PRIMARY - LOANS', 'PRIMARY - LOANS', 'NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY')OR CCC_STRATEGY IN ('NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY'))) GROUP BY COB_DATE, CCC_BUSINESS_AREA, BOOK, COUNTRY_CD_OF_RISK, CASE WHEN TERM_NEW <= 5 THEN '0-5y' WHEN TERM_NEW > 5 AND TERM_NEW <= 10 THEN '5-10y' WHEN TERM_NEW > 10 THEN '10y+' END, CASE WHEN PRODUCT_TYPE_CODE in ('GVTBOND','GVTBONDIL','BONDFUT','GOVTBONDOPT') THEN 'Government Bonds' WHEN (PRODUCT_TYPE_CODE in ('BOND','AGN') AND PRODUCT_HIERARCHY_LEVEL7 IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE','UNKNOWN')) THEN 'Covered Bonds' WHEN (PRODUCT_TYPE_CODE in ('BOND','AGN') AND PRODUCT_HIERARCHY_LEVEL7 NOT IN ('COVERED BOND-OTHER','HYPOTHEKEN PFANDBRIEFE','JUMBO PFANDBRIEFE','PFANDBRIEFE','UNKNOWN')) THEN 'Supranationals and Agencies' WHEN PRODUCT_TYPE_CODE in ('CRDBSKT','CRDINDEX','DEFSWAP') THEN 'Credit Products' WHEN PRODUCT_TYPE_CODE in ('ABS','RMBS','RMBS_ARM','RMBS_FIX') THEN 'ABS/RMBS' WHEN PRODUCT_TYPE_CODE NOT IN ('GVTBOND','GVTBONDIL','BONDFUT','GOVTBONDOPT','BOND','AGN','CRDBSKT','CRDINDEX','DEFSWAP','ABS','RMBS_ARM') THEN 'Other' END, PRODUCT_HIERARCHY_LEVEL7, CCC_PRODUCT_LINE, PRODUCT_TYPE_CODE, SECTYPE_GROUP