SELECT A.COB_DATE, 'ABS' AS CATEGORY, SUM(USD_EXPOSURE)/1000000 AS EXPOSURE FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND VERTICAL_SYSTEM <> 'PIPELINE_NY' AND VAR_EXCL_FL <> 'Y' AND A.ISSUER_COUNTRY_TIER = '1' AND A.CREDIT_TYPE_CD = 'SECURITIZED T1T2' AND A.PRODUCT_TYPE_CODE NOT IN ('AGN') AND A.DIVISION_GROUP IN ('ISG CORE') AND ( ( BREAKOUT_LINE = 'ABS - Agency' AND DIVISION_GROUP = 'ISG CORE' ) OR BREAKOUT_LINE = 'ABS - Non-Agency' ) GROUP BY A.COB_DATE UNION ALL SELECT A.COB_DATE, CASE WHEN SPG_PRODUCT_TYPE_GROUP IN ('RESI- NON AGENCY', 'SUBPRIME') THEN 'RESI' WHEN SPG_PRODUCT_TYPE_GROUP IN ('CMBS') THEN 'CMBS' ELSE NULL END AS CATEGORY, SUM(A.USD_EXPOSURE)/1000000 AS EXPOSURE FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND VAR_EXCL_FL <> 'Y' AND BU_RISK_SYSTEM <> 'PIPELINE_NY' AND CCC_BUSINESS_AREA NOT LIKE ('CPM%') AND CCC_STRATEGY NOT IN ('CORE MPE CVA', 'MONOLINE MPE CVA') AND SPG_PRODUCT_TYPE_GROUP IN ('RESI- NON AGENCY', 'SUBPRIME', 'CMBS') AND SPG_DESC NOT LIKE '%AGENCY CMBS%' AND A.CMBS_DIVISION_GROUP IN ('ISG CORE') GROUP BY A.COB_DATE, CASE WHEN SPG_PRODUCT_TYPE_GROUP IN ('RESI- NON AGENCY', 'SUBPRIME') THEN 'RESI' WHEN SPG_PRODUCT_TYPE_GROUP IN ('CMBS') THEN 'CMBS' ELSE NULL END