SELECT     a.COB_DATE,     a.REFERENCE_ENTITY_NAME,     SUM(a.USD_INDEX_PV01) as INDEX_PV01 FROM CDWUSER.U_EXP_MSR a WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and CCC_PL_REPORTING_REGION IN ('JAPAN') /*ASKED BY SAMMY*/     AND A.CCC_DIVISION='FIXED INCOME DIVISION'     AND (a.CCC_BUSINESS_AREA IN ('CPM TRADING (MPE)', 'CPM', 'CREDIT', 'MS CVA MNE - FID', 'MS CVA MNE - COMMOD') OR      a.CCC_STRATEGY IN ('MS CVA MPE - DERIVATIVES', 'MS CVA MNE - DERIVATIVES')) AND     a.ccc_product_line NOT IN ('CREDIT LOAN PORTFOLIO', 'CMD STRUCTURED FINANCE') AND     A.CURVE_TYPE NOT IN ('CPCR_CLEAR') AND     a.USD_INDEX_PV01 IS NOT NULL GROUP BY     a.COB_DATE,     a.REFERENCE_ENTITY_NAME